mapper.select:
select 

from shop
where id = #{id,jdbcType=VARCHAR}
  and user = #{user,jdbcType=VARCHAR}

mapper.delete:
delete from shop
where id = #{id,jdbcType=VARCHAR}
  and user = #{user,jdbcType=VARCHAR}

mapper.select:
select count(*) from shop

  
    and user = #{user}
  
  
    and id = #{id}

select * from shop

  
    and user = #{user}
  
  
    and id = #{id}
  

order by id desc

  limit ${offset},${limit}
